const secondHand=document.querySelector(".second.hand"),minuteHand=document.querySelector(".minute.hand"),hourHand=document.querySelector(".hour.hand"),secondText=document.getElementById("second_text");function setDate(){const e=new Date,t=e.getSeconds(),n=e.getMinutes(),o=e.getHours()>12?e.getHours()-12:e.getHours(),r=e.getHours()>=12?"PM":"AM";function d(e,t){const n=document.getElementById(e),o=t.toLocaleString("en-US",{minimumIntegerDigits:2,useGrouping:!1});n.innerHTML=`${o}`}d("second_text",t),d("minute_text",n),d("hour_text",o);document.getElementById("ampm_text").innerHTML=`${r}`;const s=6*t,u=6*n+t/10,c=30*o+.5*n;secondHand.style.transform=`rotate(${s}deg)`,minuteHand.style.transform=`rotate(${u}deg)`,hourHand.style.transform=`rotate(${c}deg)`}setInterval(setDate,1e3),setDate();